import React, { useState } from "react";

import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import "./Expenses.css";
import ExpenseFilter from "./ExpensesFilter";

const Expenses = (props) => {
	const [filteredYear, setFilteredYear] = useState("2020");

	const filterChangeHandler = (selectedYear) => {
		setFilteredYear(selectedYear);
	};

	const changedExpnseDateFormat = Object.assign({}, props.items, {
		date: props.items.date.getfullYear,
	});
	console.log(changedExpnseDateFormat, props.items);

	const filteredExpneseList = props.items.filter((d) => {
		return (d.date.getfullYear = filteredYear);
	});
	console.log("test", filteredExpneseList);

	return (
		<Card className="expenses">
			<ExpenseFilter
				selected={filteredYear}
				onChangeFilter={filterChangeHandler}
			/>
			{filteredExpneseList.map((expense) => (
				<ExpenseItem
					key={expense.id}
					title={expense.title}
					amount={expense.amount}
					date={expense.date}
				/>
			))}
		</Card>
	);
};

export default Expenses;
